import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import './Hero.css';

const Hero = () => {
  const navigate = useNavigate();
  const menuRef = useRef(null);
  const [menuOpen, setMenuOpen] = useState(false);

  /* ---------- Navigation ---------- */
  const closeMenu = () => setMenuOpen(false);

  const handleHireClick = () => {
    closeMenu();
    navigate('/HireForm');
  };

  const handleResumeClick = () => {
    closeMenu();
    window.open('/resume.pdf', '_blank');
  };

  const handleScroll = (y) => {
    closeMenu();
    window.scrollTo({ top: y, behavior: 'smooth' });
  };

  const handleDesignsClick = () => {
    closeMenu();
    window.open(
      'https://www.figma.com/design/W7L73DZ35bQVnbNHfArUS0/Untitled',
      '_blank'
    );
  };

  /* ---------- Close menu on outside click ---------- */
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (menuRef.current && !menuRef.current.contains(e.target)) {
        setMenuOpen(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  /* ---------- Typing Effect ---------- */
  const introWords = ["I'm", "EKTA", "SAJWAN"];
  const [displayedText, setDisplayedText] = useState("");
  const [wordIndex, setWordIndex] = useState(0);

  useEffect(() => {
    if (wordIndex < introWords.length) {
      const timeout = setTimeout(() => {
        setDisplayedText(prev =>
          prev ? `${prev} ${introWords[wordIndex]}` : introWords[wordIndex]
        );
        setWordIndex(wordIndex + 1);
      }, 400);
      return () => clearTimeout(timeout);
    }
  }, [wordIndex, introWords]);

  return (
    <section className="hero">

      {/* ‚≠ê Stars */}
      <div className="stars-layer">
        {[...Array(40)].map((_, i) => (
          <div
            key={i}
            className="star"
            style={{
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
            }}
          />
        ))}
      </div>

      {/* ‚ò∞ Hamburger */}
      <button
        className="menu-icon"
        aria-label="Open menu"
        onClick={() => setMenuOpen(!menuOpen)}
      >
        ‚ò∞
      </button>

      {/* üîò Menu */}
      <div
        ref={menuRef}
        className={`top-right ${menuOpen ? 'open' : ''}`}
      >
        <button onClick={handleResumeClick}>RESUME</button>
        <button onClick={() => handleScroll(900)}>PROJECTS</button>
        <button onClick={handleDesignsClick}>DESIGNS</button>
        <button onClick={() => handleScroll(1600)}>CONTACT ME</button>
        <button className="hire" onClick={handleHireClick}>HIRE ME</button>
      </div>

      {/* üßæ Intro */}
      <div className="rectangle slide-in-left">
        <div className="text">
          <h1>
            Hello!! <span className="highlight">{displayedText}</span>
          </h1>
          <p className="fade-in">
            A passionate Developer creating modern digital experiences.
          </p>
          <p className="fade-in delay">
            Working at PPS INTERNATIONAL as a GET (R&D)
          </p>
        </div>
      </div>

    </section>
  );
};

export default Hero;
