import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Hero.css';

const Hero = () => {
  const navigate = useNavigate();
  const [menuOpen, setMenuOpen] = useState(false);

  const handleHireClick = () => navigate('/HireForm');
  const handleResumeClick = () => window.open('/resume.pdf', '_blank');
  const handleProjectsClick = () => window.scrollTo({ top: 800, behavior: 'smooth' });
  const handleDesignsClick = () =>
    window.open(
      'https://www.figma.com/design/W7L73DZ35bQVnbNHfArUS0/Untitled?node-id=0-1&t=yxhk86muU1HO1hEC-1',
      '_blank'
    );
  const handleContactClick = () => window.scrollTo({ top: 1500, behavior: 'smooth' });

  const introWords = ["I'm", "EKTA", "SAJWAN"];
  const [displayedText, setDisplayedText] = useState("");
  const [wordIndex, setWordIndex] = useState(0);

  useEffect(() => {
    if (wordIndex < introWords.length) {
      const timeout = setTimeout(() => {
        setDisplayedText(prev => prev + (prev ? " " : "") + introWords[wordIndex]);
        setWordIndex(wordIndex + 1);
      }, 400);
      return () => clearTimeout(timeout);
    }
  }, [wordIndex]);

  return (
    <section className="hero">

      {/* ‚≠ê Stars */}
      <div className="stars-layer">
        {[...Array(40)].map((_, i) => (
          <div
            key={i}
            className="star twinkle"
            style={{
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
            }}
          />
        ))}
      </div>

      {/* ‚ò∞ Hamburger */}
      <div className="menu-icon" onClick={() => setMenuOpen(!menuOpen)}>
        ‚ò∞
      </div>

      {/* üîò Buttons */}
      <div className={`top-right ${menuOpen ? 'open' : ''}`}>
        <button onClick={handleResumeClick}>RESUME</button>
        <button onClick={handleProjectsClick}>PROJECTS</button>
        <button onClick={handleDesignsClick}>DESIGNS</button>
        <button onClick={handleContactClick}>CONTACT ME</button>
        <button onClick={handleHireClick}>HIRE ME</button>
      </div>

      {/* üßæ Intro */}
      <div className="rectangle slide-in-left">
        <div className="text">
          <h1>Hello!! <span className="highlight">{displayedText}</span></h1>
          <p className="fade-in">A passionate Developer creating modern digital experiences.</p>
          <p className="fade-in delay">Working at PPS INTERNATIONAL as a GET (R&D)</p>
        </div>
      </div>
    </section>
  );
};

export default Hero;
