import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./Hero.css";

const Hero = () => {
  const navigate = useNavigate();

  const words = ["I'm", "Ekta", "Sajwan,", "a", "Software", "Developer"];
  const [text, setText] = useState("");
  const [index, setIndex] = useState(0);

  useEffect(() => {
    if (index < words.length) {
      const timeout = setTimeout(() => {
        setText((prev) => prev + (prev ? " " : "") + words[index]);
        setIndex(index + 1);
      }, 400);
      return () => clearTimeout(timeout);
    }
  }, [index]);

  return (
    <section className="hero">

      {/* Navbar */}
      <div className="top-nav">
        <div className="nav-buttons">
          <button onClick={() => navigate("/resume")}>RESUME</button>
          <button onClick={() => navigate("/projects")}>PROJECTS</button>
          <button onClick={() => navigate("/designs")}>DESIGNS</button>
          <button onClick={() => navigate("/HireForm")}>CONTACT ME</button>
          <button 
            className="hire-btn"
            onClick={() => navigate("/HireForm")}
          >
            HIRE ME
          </button>
        </div>
      </div>

      {/* Hero Content */}
      <div className="hero-content">
        <div className="hero-text">
          <h1>
            Hey there, <br />
            <span className="highlight">{text}</span>
          </h1>

          <p>
            A passionate developer creating modern digital experiences.
          </p>
        </div>
      </div>

    </section>
  );
};

export default Hero;
