import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./Hero.css";

const Hero = () => {
  const navigate = useNavigate();
  const [menuOpen, setMenuOpen] = useState(false);

  const words = ["I'm", "EKTA", "SAJWAN" ,];
  const [text, setText] = useState("");
  const [index, setIndex] = useState(0);

  useEffect(() => {
    if (index < words.length) {
      const timeout = setTimeout(() => {
        setText((prev) => prev + (prev ? " " : "") + words[index]);
        setIndex(index + 1);
      }, 400);

      return () => clearTimeout(timeout);
    }
  }, [index]);

  return (
    <section className="hero">
      <div className="nav-icon" onClick={() => setMenuOpen(!menuOpen)}>
        â˜°
      </div>

      {menuOpen && (
        <div className="dropdown-menu">
          <button onClick={() => window.open("/resume.pdf", "_blank")}>
            Resume
          </button>
          <button onClick={() => navigate("/HireForm")}>
            Hire Me
          </button>
        </div>
      )}

      <div className="text">
        <h1>
          Hello!! <span className="highlight">{text}</span>
        </h1>
        <p className="fade-in">
          A passionate Developer creating modern digital experiences.
        </p>
        <p className="fade-in delay">
          Working at PPS INTERNATIONAL as GET (R&D)
        </p>
      </div>
    </section>
  );
};

export default Hero;
