import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./Hero.css";

const Hero = () => {
  const navigate = useNavigate();
  const [menuOpen, setMenuOpen] = useState(false);

  const handleHireClick = () => navigate("/HireForm");
  const handleResumeClick = () => window.open("/resume.pdf", "_blank");
  const handleProjectsClick = () =>
    window.scrollTo({ top: 800, behavior: "smooth" });
  const handleDesignsClick = () =>
    window.open(
      "https://www.figma.com/design/W7L73DZ35bQVnbNHfArUS0/Untitled?node-id=0-1&t=yxhk86muU1HO1hEC-1",
      "_blank"
    );
  const handleContactClick = () =>
    window.scrollTo({ top: 1500, behavior: "smooth" });

  const introWords = ["I'm", "EKTA", "SAJWAN"];
  const [displayedText, setDisplayedText] = useState("");
  const [wordIndex, setWordIndex] = useState(0);

  useEffect(() => {
    if (wordIndex < introWords.length) {
      const timeout = setTimeout(() => {
        setDisplayedText(
          (prev) => prev + (prev ? " " : "") + introWords[wordIndex]
        );
        setWordIndex(wordIndex + 1);
      }, 400);
      return () => clearTimeout(timeout);
    }
  }, [wordIndex]);

  return (
    <section className="hero">
      {/* Hamburger Icon */}
      <div className="nav-icon" onClick={() => setMenuOpen(!menuOpen)}>
        â˜°
      </div>

      {/* Dropdown Menu */}
      {menuOpen && (
        <div className="dropdown-menu">
          <button onClick={handleResumeClick}>Resume</button>
          <button onClick={handleProjectsClick}>Projects</button>
          <button onClick={handleDesignsClick}>Designs</button>
          <button onClick={handleContactClick}>Contact</button>
          <button onClick={handleHireClick}>Hire Me</button>
        </div>
      )}

      <div className="text">
        <h1>
          Hello!! <span className="highlight">{displayedText}</span>
        </h1>
        <p className="fade-in">
          A passionate Developer creating modern digital experiences.
        </p>
        <p className="fade-in delay">
          Working at PPS INTERNATIONAL as a GET (R&D)
        </p>
      </div>
    </section>
  );
};

export default Hero;
